mapper.select:
SELECT o.id, p.product_name, DATE_FORMAT( o.appoint_time, '%Y-%m-%d' ) AS appoint_time, appoint_money, DATE_ADD(r.establish_time,INTERVAL o.date_num MONTH) as establish_time FROM p_order AS o LEFT JOIN p_product AS p ON p.id = o.product_id LEFT JOIN p_raise as r on o.raise_id = r.id WHERE p.product_class = '权益类' AND o.`status` IN ( 2, 4 ) AND o.id
NOT
IN ( SELECT order_id FROM p_order_out WHERE card_num = #{cardNum} ) AND o.card_num = #{cardNum}
