mapper.select:
SELECT u.*, p.organization_name orgName FROM sys_user u LEFT JOIN sys_user_role r ON u.user_id = r.user_id LEFT JOIN sys_org_user g on g.user_id=u.user_id LEFT JOIN sys_org_group p on p.id=g.org_id
and (u.username like CONCAT('%',#{params.username},'%') or u.realname like CONCAT('%',#{params.username},'%'))
and u.create_user_id = #{params.createUserId}
and r.role_id = #{params.roleId}
and r.role_id = #{params.roleId}
and g.org_id in ( ${params.orgId})
and p.id is null
GROUP BY u.user_id order by create_time desc

select distinct rm.menu_id from sys_user_role ur 
	LEFT JOIN sys_role_menu rm on ur.role_id = rm.role_id 
where ur.user_id = #{userId}

SELECT user_id, realname, person_photos, mobile FROM sys_user WHERE user_id IN ( SELECT user_id FROM sys_org_user WHERE org_id IN
#{orgGroupId}
)
